  #{status,jdbcType=INTEGER}, #{createAt,jdbcType=BIGINT}, #{updateAt,jdbcType=BIGINT},
  #{updateBy,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, #{workId,jdbcType=BIGINT}
  )

mapper.select:
