  #{peopleName,jdbcType=VARCHAR}, #{peopleImage,jdbcType=VARCHAR}, #{peopleDetail,jdbcType=VARCHAR}, 
  #{peopleProductionIntro,jdbcType=VARCHAR}, #{peopleContactWay,jdbcType=VARCHAR}, 
  #{ifArtist,jdbcType=BIT}, #{ifWorker,jdbcType=BIT}, #{peoplePutaway,jdbcType=BIT}
  )
