mapper.select:
select * from comment

  
    status=#{status}
  
  
    AND from_by=#{fromBy}

mapper.insert:
insert into comment (id, nick, work_id,
  from_by, to_by, type, status,
  content, create_at, update_at
  )
values (#{id,jdbcType=INTEGER}, #{nick,jdbcType=VARCHAR}, #{workId,jdbcType=INTEGER},
  #{fromBy,jdbcType=VARCHAR}, #{toBy,jdbcType=VARCHAR}, #{type,jdbcType=BIT}, #{status,jdbcType=BIT},
  #{content,jdbcType=VARCHAR}, #{createAt,jdbcType=BIGINT}, #{updateAt,jdbcType=BIGINT}
  )
