mapper.select:
select * from work

  
    status=#{status}
  
  
    AND update_by=#{updateBy}

mapper.insert:
insert into work (id, name, first_id,
  second_id, status, introduce,
  thumbnail, url, content,
  create_at, update_at, update_by
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{firstId,jdbcType=INTEGER},
  #{secondId,jdbcType=INTEGER}, #{status,jdbcType=BIT}, #{introduce,jdbcType=VARCHAR},
  #{thumbnail,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR},
  #{createAt,jdbcType=BIGINT}, #{updateAt,jdbcType=BIGINT}, #{updateBy,jdbcType=VARCHAR}
  )
